def extract_title_from_filename(filename):
    """
    ãƒ•ã‚¡ã‚¤ãƒ«åã‹ã‚‰ã€å±€åã€‘ã¨æ‹¡å¼µå­(.pdf)ã‚’å–ã‚Šé™¤ã„ã¦ã€ãã‚Œã‚’ã€Œä»¶åã€ã¨ã™ã‚‹é–¢æ•°
    """
    # 1. å†’é ­ã®ã€ã€‡ã€‡å±€ã€‘ã‚’å‰Šé™¤ï¼ˆã€ä»»æ„ã®æ–‡å­—ã€‘ã®ãƒ‘ã‚¿ãƒ¼ãƒ³ï¼‰
    name = re.sub(r'^ã€[^ã€‘]+ã€‘', '', filename)
    # 2. æœ«å°¾ã®æ‹¡å¼µå­(.pdf)ã‚’å‰Šé™¤ï¼ˆå¤§æ–‡å­—å°æ–‡å­—ç„¡è¦–ï¼‰
    name = re.sub(r'\.pdf$', '', name, flags=re.IGNORECASE)
    # 3. å‰å¾Œã®ç©ºç™½å‰Šé™¤
    return name.strip()

def call_gemini_with_retry(model, prompt, file_bytes, max_retries=3):
    """
    429ã‚¨ãƒ©ãƒ¼ï¼ˆRate Limitï¼‰ãŒå‡ºãŸå ´åˆã«è‡ªå‹•ã§å¾…æ©Ÿã—ã¦ãƒªãƒˆãƒ©ã‚¤ã™ã‚‹é–¢æ•°
    """
    for attempt in range(max_retries):
        try:
            response = model.generate_content([
                prompt,
                {"mime_type": "application/pdf", "data": file_bytes}
            ])
            return response
        except Exception as e:
            error_str = str(e)
            # 429ã‚¨ãƒ©ãƒ¼ï¼ˆResourceExhaustedï¼‰ã®å ´åˆã®ã¿ãƒªãƒˆãƒ©ã‚¤
            if "429" in error_str or "ResourceExhausted" in error_str or "Quota exceeded" in error_str:
                if attempt < max_retries - 1:
                    # å¾…æ©Ÿæ™‚é–“ï¼ˆå¾ã€…ã«é•·ãã™ã‚‹ + ãƒ©ãƒ³ãƒ€ãƒ è¦ç´ ã§åˆ†æ•£ï¼‰
                    wait_time = (2 ** attempt) + random.uniform(0, 1)
                    st.toast(f"âš ï¸ æ··é›‘ä¸­... {int(wait_time)}ç§’å¾…æ©Ÿã—ã¦å†è©¦è¡Œã—ã¾ã™ ({attempt+1}/{max_retries})")
                    time.sleep(wait_time)
                    continue
            # ãã®ä»–ã®ã‚¨ãƒ©ãƒ¼ã¾ãŸã¯ãƒªãƒˆãƒ©ã‚¤åˆ‡ã‚Œã®å ´åˆã¯ä¾‹å¤–ã‚’æŠ•ã’ã‚‹
            raise e

